let $=window.jQuery;$(document).ready(function(){$(".carousel").carousel({pause:true});$(".carousel").bind("slide.bs.carousel",function(e){const scrollPosition=document.documentElement.scrollTop||document.body.scrollTop;window.scrollTo({left:0,top:scrollPosition})})});document.addEventListener("DOMContentLoaded",function(){const navLinks=document.querySelectorAll(".navbar-nav a.nav-link");const menuToggle=document.getElementById("dropdown");const mainMenu=document.getElementById("nav");window.scrollTo(0,0);document.getElementById("loading-screen").style.display="block";const path=window.location.pathname;if(path!=="/"){const homePages=document.querySelectorAll(".home-page");homePages.forEach(function(homePage){homePage.classList.add("hidden")})}if(path!=="/riznica"){const homePages=document.querySelectorAll(".riznica");homePages.forEach(function(homePage){homePage.classList.add("hidden")})}setTimeout(function(){document.getElementById("loading-screen").style.opacity=0;setTimeout(function(){document.getElementById("loading-screen").style.display="none"},500)},3e3);function isInViewport(element){var rect=element.getBoundingClientRect();return rect.top>=0&&rect.left>=0&&rect.bottom<=(window.innerHeight||document.documentElement.clientHeight)&&rect.right<=(window.innerWidth||document.documentElement.clientWidth)}function activateLink(){var sections=document.querySelectorAll(".nav-link");sections.forEach(function(section){var link=document.querySelector('a[href="#'+section.id+'"]');if(isInViewport(section)){link.classList.add("active")}else{link.classList.remove("active")}})}activateLink();window.addEventListener("scroll",function(){activateLink()});navLinks.forEach(link=>{link.addEventListener("click",function(){navLinks.forEach(navLink=>navLink.classList.remove("active"));this.classList.add("active")})});menuToggle.addEventListener("change",function(){if(menuToggle.checked){mainMenu.classList.add("show")}else{mainMenu.classList.remove("show")}})});