const mysql=require("mysql2");const express=require("express");require("dotenv").config();const app=express();app.use(express.json());const con=mysql.createConnection({host:process.env.DB_HOST,user:process.env.DB_USER,password:process.env.DB_PASS,database:process.env.DB_NAME});app.get("/api/kalendar",(req,res)=>{con.connect(function(err){if(err)throw err;var sql=`SELECT * FROM kalendar`;con.query(sql,function(err,result){if(err)throw err;res.json(result)})})});app.post("/api/kalendar",(req,res)=>{const{praznik,stari,novi,post,ime_dana,mesec}=req.body;con.connect(function(err){if(err)throw err;var sql=`INSERT INTO kalendar (praznik, stari, novi, post, ime_dana, mesec) VALUES (?, ?, ?, ?, ?, ?)`;con.query(sql,[praznik,stari,novi,post,ime_dana,mesec],function(err,result){if(err)throw err;con.query("SELECT * FROM kalendar WHERE id = ?",[result.insertId],function(err,record){if(err)throw err;res.json({message:"Record inserted",record:record[0]})})})})});app.get("/api/kalendar/mesec/:mesec",(req,res)=>{const{mesec}=req.params;con.connect(function(err){if(err)throw err;var sql=`SELECT * FROM kalendar WHERE mesec = ?`;con.query(sql,[mesec],function(err,result){if(err)throw err;res.json(result)})})});